mapper.update:
update t_delivery_item
set sd_flag = 1
where delivery_code = #{delivery_code} and item_code = #{item_code}

update t_delivery_item
set sd_flag = 0
where item_code = #{item_code}

mapper.select:
select sum(number)
from t_bad_item
where delivery_code = #{delivery_code} and item_code = #{item_code}
