mapper.update:
update t_delivery
set r_flag=1,
    box_label=#{boxLabel},
    box_area=#{boxArea}
where code = #{code}

update t_batch
set r_flag=1
where code = #{code}

update t_delivery_item
set number=number - #{quantity},
    c_flag=if(number = 0, 1, 0)
where delivery_code = #{deliveryCode}
  and item_code = #{itemCode}

update t_delivery
set c_flag=1
where code = #{code}

update t_batch
set c_flag=1
where code = #{code}
